# Cell

import requests
from bs4 import BeautifulSoup

# Cell
def make_soup(

     url : str
    ,parser : str = 'html.parser'
    ,**request_kwargs

)->BeautifulSoup:
    """ Returns a Beautifulsoup object for the passed URL."""

    # TODO : error handling?
    request = requests.get(url=url,**request_kwargs)
    return BeautifulSoup(request.content,parser)