def soma(num1, num2):
    print(f'A soma dos numeros eh igual é: {num1+num2}')

def sub(num1, num2):
    print(f'A soma dos numeros eh igual é: {num1-num2}')

def mult(num1, num2):
    print(f'A soma dos numeros eh igual é: {num1*num2}')

def divi(num1, num2):
    print(f'A soma dos numeros eh igual é: {num1/num2}')

